figure('InvertHardcopy','off','PaperSize',[15 15],'Color',[1 1 1]);

% Create axes
axes1 = axes;
hold(axes1,'on');

% Create multiple lines using matrix input to plot
plot1 = plot(X1,YMatrix1,'LineWidth',2);
set(plot1(1),'DisplayName','Motor A','Marker','v');
