set(plot1(3),'DisplayName','Motor C','Marker','^');

% Create ylabel
ylabel('$U\;[V]$','FontSize',17.6,'Interpreter','latex');

% Create xlabel
xlabel('$PWM\;[-]$','FontSize',17.6,'Interpreter','latex');
yLim = [0 12];
