% xlim(axes1,[0 1]);
% Uncomment the following line to preserve the Y-limits of the axes
% ylim(axes1,[0 12]);
% Uncomment the following line to preserve the Z-limits of the axes
% zlim(axes1,[-1 1]);
box(axes1,'on');
grid(axes1,'on');
axis(axes1,'square');
% Set the remaining axes properties
set(axes1,'FontSize',16,'GridAlpha',1,'GridLineStyle',':','MinorGridAlpha',...
    1,'XMinorTick','on','XTick',[0 0.2 0.4 0.6 0.8 1],'XTickLabel',...
    {'0','0.2','0.4','0.6','0.8','1'},'YMinorTick','on','YTick',...
    [0 2 4 6 8 10 12],'YTickLabel',{'0','2','4','6','8','10','12'});
